contracts/GreenFI.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";

contract GreenFi is Initializable, UUPSUpgradeable, OwnableUpgradeable, ReentrancyGuardUpgradeable {
    using SafeERC20 for IERC20;

    // WEEDL Token Address
    IERC20 public weedlToken;

    // Structs
    struct LandLoan {
        string id;
        string title;
        string description;
        uint256 size;
        string collateralToken; // "ETH" or "WEEDL"
        uint256 collateralAmount;
        uint256 termMonths;
        uint256 interestRate; // In basis points (1% = 100)
        uint256 yieldScore; // In basis points (e.g., 4.5/5 = 450)
        address owner;
        bool isLent;
        address borrower;
    }

    struct CryptoLoan {
        string id;
        string title;
        string description;
        uint256 amountETH;
        string collateral; // "ETH" or "WEEDL"
        uint256 interestRate; // In basis points
        uint256 termMonths;
        uint256 yieldScore; // In basis points
        string relatedLandId;
        address owner;
        bool isActive;
    }

    // State Variables
    mapping(string => LandLoan) public landLoans;
    mapping(string => CryptoLoan) public cryptoLoans;
    mapping(address => uint256) public stakedBalances;
    string[] public landLoanIds;
    string[] public cryptoLoanIds;
    uint256 public totalStaked;

    // Events
    event LandListed(string indexed id, address indexed owner, string title, uint256 collateralAmount);
    event LandLent(string indexed id, address indexed borrower, uint256 collateralAmount);
    event LandUnlisted(string indexed id, address indexed owner);
    event LandRepaid(string indexed id, address indexed borrower);
    event LandRetrieved(string indexed id, address indexed owner);
    event CryptoLoanListed(string indexed id, address indexed owner, uint256 amountETH);
    event CryptoLoanFunded(string indexed id, address indexed funder, uint256 amountETH);
    event CryptoLoanUnlisted(string indexed id, address indexed owner);
    event Staked(address indexed user, uint256 amount);
    event Unstaked(address indexed user, uint256 amount);

    // Errors
    error InvalidInput();
    error NotOwner();
    error AlreadyLent();
    error NotLent();
    error InsufficientBalance();
    error InvalidLoan();
    error InvalidCollateral();

    // Initializer
    function initialize(address _weedlToken) public initializer {
        if (_weedlToken == address(0)) revert InvalidInput();
        __UUPSUpgradeable_init();
        __Ownable_init(msg.sender);
        __ReentrancyGuard_init();
        weedlToken = IERC20(_weedlToken);
    }

    // Required for UUPS upgradeability
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    // Land Loan Functions
    function listLand(
        string memory id,
        string memory title,
        string memory description,
        uint256 size,
        string memory collateralToken,
        uint256 collateralAmount,
        uint256 termMonths,
        uint256 interestRate
    ) external payable nonReentrant returns (uint256) {
        if (bytes(id).length == 0 || bytes(title).length == 0 || bytes(description).length == 0 || size == 0 || collateralAmount == 0 || termMonths == 0 || interestRate == 0) {
            revert InvalidInput();
        }
        if (keccak256(bytes(collateralToken)) != keccak256("ETH") && keccak256(bytes(collateralToken)) != keccak256("WEEDL")) {
            revert InvalidCollateral();
        }
        if (keccak256(bytes(collateralToken)) == keccak256("ETH") && msg.value < collateralAmount) {
            revert InsufficientBalance();
        }
        if (keccak256(bytes(collateralToken)) == keccak256("WEEDL")) {
            weedlToken.safeTransferFrom(msg.sender, address(this), collateralAmount);
        }

        LandLoan memory newLoan = LandLoan({
            id: id,
            title: title,
            description: description,
            size: size,
            collateralToken: collateralToken,
            collateralAmount: collateralAmount,
            termMonths: termMonths,
            interestRate: interestRate,
            yieldScore: calculateYieldScore(size, collateralAmount, termMonths, interestRate),
            owner: msg.sender,
            isLent: false,
            borrower: address(0)
        });

        landLoans[id] = newLoan;
        landLoanIds.push(id);

        emit LandListed(id, msg.sender, title, collateralAmount);
        return landLoanIds.length - 1;
    }

    function lend(string memory id) external payable nonReentrant {
        LandLoan storage loan = landLoans[id];
        if (loan.owner == address(0)) revert InvalidLoan();
        if (loan.isLent) revert AlreadyLent();
        if (keccak256(bytes(loan.collateralToken)) == keccak256("ETH") && msg.value < loan.collateralAmount) {
            revert InsufficientBalance();
        }
        if (keccak256(bytes(loan.collateralToken)) == keccak256("WEEDL")) {
            weedlToken.safeTransferFrom(msg.sender, address(this), loan.collateralAmount);
        }

        loan.isLent = true;
        loan.borrower = msg.sender;

        emit LandLent(id, msg.sender, loan.collateralAmount);
    }

    function unlist(string memory id) external nonReentrant {
        LandLoan storage loan = landLoans[id];
        if (loan.owner == address(0)) revert InvalidLoan();
        if (loan.owner != msg.sender) revert NotOwner();
        if (loan.isLent) revert AlreadyLent();

        if (keccak256(bytes(loan.collateralToken)) == keccak256("ETH")) {
            payable(msg.sender).transfer(loan.collateralAmount);
        } else {
            weedlToken.safeTransfer(msg.sender, loan.collateralAmount);
        }

        delete landLoans[id];
        for (uint256 i = 0; i < landLoanIds.length; i++) {
            if (keccak256(bytes(landLoanIds[i])) == keccak256(bytes(id))) {
                landLoanIds[i] = landLoanIds[landLoanIds.length - 1];
                landLoanIds.pop();
                break;
            }
        }

        emit LandUnlisted(id, msg.sender);
    }

    function repay(string memory id) external payable nonReentrant {
        LandLoan storage loan = landLoans[id];
        if (loan.owner == address(0)) revert InvalidLoan();
        if (!loan.isLent || loan.borrower != msg.sender) revert NotLent();
        if (keccak256(bytes(loan.collateralToken)) == keccak256("ETH") && msg.value < loan.collateralAmount) {
            revert InsufficientBalance();
        }
        if (keccak256(bytes(loan.collateralToken)) == keccak256("WEEDL")) {
            weedlToken.safeTransferFrom(msg.sender, address(this), loan.collateralAmount);
        }

        loan.isLent = false;
        loan.borrower = address(0);

        emit LandRepaid(id, msg.sender);
    }

    function retrieve(string memory id) external nonReentrant {
        LandLoan storage loan = landLoans[id];
        if (loan.owner == address(0)) revert InvalidLoan();
        if (loan.owner != msg.sender) revert NotOwner();
        if (loan.isLent) revert AlreadyLent();

        if (keccak256(bytes(loan.collateralToken)) == keccak256("ETH")) {
            payable(msg.sender).transfer(loan.collateralAmount);
        } else {
            weedlToken.safeTransfer(msg.sender, loan.collateralAmount);
        }

        delete landLoans[id];
        for (uint256 i = 0; i < landLoanIds.length; i++) {
            if (keccak256(bytes(landLoanIds[i])) == keccak256(bytes(id))) {
                landLoanIds[i] = landLoanIds[landLoanIds.length - 1];
                landLoanIds.pop();
                break;
            }
        }

        emit LandRetrieved(id, msg.sender);
    }

    function getLandLoans() external view returns (LandLoan[] memory) {
        LandLoan[] memory result = new LandLoan[](landLoanIds.length);
        for (uint256 i = 0; i < landLoanIds.length; i++) {
            result[i] = landLoans[landLoanIds[i]];
        }
        return result;
    }

    // Crypto Loan Functions
    function listCryptoLoan(
        string memory id,
        string memory title,
        string memory description,
        uint256 amountETH,
        string memory collateral,
        uint256 termMonths,
        uint256 interestRate
    ) external payable nonReentrant returns (uint256) {
        if (bytes(id).length == 0 || bytes(title).length == 0 || bytes(description).length == 0 || amountETH == 0 || termMonths == 0 || interestRate == 0) {
            revert InvalidInput();
        }
        if (keccak256(bytes(collateral)) != keccak256("ETH") && keccak256(bytes(collateral)) != keccak256("WEEDL")) {
            revert InvalidCollateral();
        }
        if (keccak256(bytes(collateral)) == keccak256("ETH") && msg.value < amountETH) {
            revert InsufficientBalance();
        }
        if (keccak256(bytes(collateral)) == keccak256("WEEDL")) {
            weedlToken.safeTransferFrom(msg.sender, address(this), amountETH);
        }

        CryptoLoan memory newLoan = CryptoLoan({
            id: id,
            title: title,
            description: description,
            amountETH: amountETH,
            collateral: collateral,
            interestRate: interestRate,
            termMonths: termMonths,
            yieldScore: calculateYieldScore(0, amountETH, termMonths, interestRate),
            relatedLandId: "",
            owner: msg.sender,
            isActive: true
        });

        cryptoLoans[id] = newLoan;
        cryptoLoanIds.push(id);

        emit CryptoLoanListed(id, msg.sender, amountETH);
        return cryptoLoanIds.length - 1;
    }

    function fund(string memory id) external payable nonReentrant {
        CryptoLoan storage loan = cryptoLoans[id];
        if (loan.owner == address(0)) revert InvalidLoan();
        if (!loan.isActive) revert InvalidLoan();
        if (msg.value < loan.amountETH) revert InsufficientBalance();

        loan.isActive = false;
        payable(loan.owner).transfer(loan.amountETH);

        emit CryptoLoanFunded(id, msg.sender, loan.amountETH);
    }

    function unlistCryptoLoan(string memory id) external nonReentrant {
        CryptoLoan storage loan = cryptoLoans[id];
        if (loan.owner == address(0)) revert InvalidLoan();
        if (loan.owner != msg.sender) revert NotOwner();
        if (!loan.isActive) revert InvalidLoan();

        if (keccak256(bytes(loan.collateral)) == keccak256("ETH")) {
            payable(msg.sender).transfer(loan.amountETH);
        } else {
            weedlToken.safeTransfer(msg.sender, loan.amountETH);
        }

        delete cryptoLoans[id];
        for (uint256 i = 0; i < cryptoLoanIds.length; i++) {
            if (keccak256(bytes(cryptoLoanIds[i])) == keccak256(bytes(id))) {
                cryptoLoanIds[i] = cryptoLoanIds[cryptoLoanIds.length - 1];
                cryptoLoanIds.pop();
                break;
            }
        }

        emit CryptoLoanUnlisted(id, msg.sender);
    }

    function getCryptoLoans() external view returns (CryptoLoan[] memory) {
        CryptoLoan[] memory result = new CryptoLoan[](cryptoLoanIds.length);
        for (uint256 i = 0; i < cryptoLoanIds.length; i++) {
            result[i] = cryptoLoans[cryptoLoanIds[i]];
        }
        return result;
    }

    // Staking Functions
    function stake(uint256 amount) external nonReentrant {
        if (amount == 0) revert InvalidInput();
        weedlToken.safeTransferFrom(msg.sender, address(this), amount);
        stakedBalances[msg.sender] += amount;
        totalStaked += amount;

        emit Staked(msg.sender, amount);
    }

    function unstake(uint256 amount) external nonReentrant {
        if (amount == 0 || stakedBalances[msg.sender] < amount) revert InvalidInput();
        stakedBalances[msg.sender] -= amount;
        totalStaked -= amount;
        weedlToken.safeTransfer(msg.sender, amount);

        emit Unstaked(msg.sender, amount);
    }

    function getStakedBalance(address account) external view returns (uint256) {
        return stakedBalances[account];
    }

    // Placeholder for yield score calculation
    function calculateYieldScore(uint256 size, uint256 collateralAmount, uint256 termMonths, uint256 interestRate) private pure returns (uint256) {
        return (size + collateralAmount / 1e18 + termMonths + interestRate) % 500; // Simplified
    }
}

contracts/WEEDL.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract WEEDL is ERC20, Ownable, ReentrancyGuard {
    // Events
    event Burn(address indexed from, uint256 amount, uint256 timestamp);

    // Errors
    error ZeroAmount();
    error ZeroAddress();
    error MaxSupplyExceeded();

    // Constants
    uint256 public constant MAX_SUPPLY = 1_000_000_000 * 1e18; // 1 billion tokens
    uint256 public totalMinted;

    constructor(address initialOwner, uint256 initialSupply) ERC20("WEEDL Token", "WEEDL") Ownable(initialOwner) {
        if (initialOwner == address(0)) revert ZeroAddress();
        if (initialSupply == 0 || initialSupply > MAX_SUPPLY) revert MaxSupplyExceeded();

        totalMinted = initialSupply;
        _mint(initialOwner, initialSupply);
    }

    // Burn tokens (caller must have sufficient balance)
    function burn(uint256 amount) external nonReentrant {
        if (amount == 0) revert ZeroAmount();
        _burn(msg.sender, amount);
        totalMinted -= amount;
        emit Burn(msg.sender, amount, block.timestamp);
    }

    // Override decimals to ensure 18 decimals
    function decimals() public pure override returns (uint8) {
        return 18;
    }
}

